import React, { useContext, useState } from 'react'
import { Steps } from 'antd';
import { userContext } from '../../../context/Usercontext';
const steps = [
    {
        title: 'First'
    },
    {
        title: 'Second'
    },
    {
        title: 'Last'
    },
];

function Salad({ salad, saladToast, qty, note, setQty, setNote, handeAddtoCart, setProductDetails }) {
    const { selectedItems, setSelectedItems } = useContext(userContext)

    const [current, setCurrent] = useState(0);
    const [saladItem, setSaladItem] = useState(null)
    const [Toast, setToast] = useState(null)


    const items = steps.map((item) => ({
        key: item.title,
        title: item.title,
    }));

    const handleSaladItem = (item) => {
        if (saladItem === item) {
            setSaladItem(null);
        } else {
            setSaladItem(item);
        }

    };

    const handlesaladToast = (item) => {
        if (Toast === item) {
            setToast(null);
        } else {
            setToast(item);
        }
    }

    console.log("saladItem", saladItem);
    console.log("Toast", Toast);
    const next = () => {
        setCurrent(current + 1)
        setSelectedItems([saladItem, Toast])
    }

    console.log("selectedItems", selectedItems);

    return (
        <>
            <div className='salad'>
                <Steps current={current} items={items} />
                {current === 0 ?
                    <>
                        <div style={{ display: "flex", justifyContent: "flex-end", margin: "20px 0" }}>
                            <div>
                                {current < steps.length - 1 && (
                                    <button disabled={!saladItem} style={{ background: saladItem ? "#4e35b1" : "#f4f4f6", color: saladItem ? "#fff" : "#4e35b1" }} onClick={() => { setCurrent(current + 1) }}>
                                        Next
                                    </button>
                                )}
                            </div>
                        </div>

                        <p style={{ textAlign: "center", fontSize: "14px", marginBottom: "10px" }}>Please Select 1 item</p>

                        {salad.map((item, index) => (
                            <div key={index} style={{ width: "100%", maxWidth: "40%", margin: "0 auto 5px auto" }}>
                                <label>
                                    <input type="checkbox" onChange={() => handleSaladItem(item)} checked={saladItem === item} />
                                    <span style={{ marginLeft: "20px" }}>{item.name} [{item.price}]</span>
                                </label>
                            </div>
                        ))}
                    </>
                    : ""}

                {current === 1 ?
                    <>
                        <div style={{ display: "flex", justifyContent: "space-between", margin: "20px 0" }}>
                            <div>
                                <button onClick={() => { setCurrent(current - 1) }} >
                                    Previous
                                </button>

                            </div>
                            {current < steps.length - 1 && (
                                <button disabled={!Toast} style={{ background: Toast ? "#4e35b1" : "#f4f4f6", color: Toast ? "#fff" : "#4e35b1" }} onClick={() => next()}>
                                    Next
                                </button>
                            )}
                        </div>
                        <p style={{ textAlign: "center", fontSize: "14px", marginBottom: "10px" }}>Please Select 1 item</p>

                        {saladToast.map((item, index) => (
                            <div key={index} style={{ width: "100%", maxWidth: "40%", margin: "0 auto 5px auto" }}>
                                <label>
                                    <input type="checkbox" onChange={() => handlesaladToast(item)} checked={Toast === item} />
                                    <span style={{ marginLeft: "20px" }}>{item.name}</span>
                                </label>
                            </div>
                        ))}
                    </>
                    : ""}

                {current === 2 ?
                    <>
                        <div style={{ display: "flex", justifyContent: "flex-start", margin: "20px 0" }}>
                            <div>
                                <button onClick={() => { setCurrent(current - 1) }} >
                                    Previous
                                </button>
                            </div>
                        </div>

                        <div className='note-outer'>
                            <span>Notes</span>
                            <textarea className='note' value={note} onChange={(e) => setNote(e.target.value)}></textarea>
                        </div>

                        <div className='qty-inc-dec'>
                            <div onClick={() => { if (qty > 1) { setQty(qty - 1) } }}>-</div>
                            <input type="text" value={qty} readOnly onChange={(e) => setQty(e.target.value)} />
                            <div onClick={() => setQty(qty + 1)}>+</div>
                        </div>
                    </>
                    : ""}

                <div className='model-footer'>
                    <button onClick={() => setProductDetails(false)} className='cancle'>CANCLE</button>

                    <button disabled={current != 2} style={{ background: current === 2 ? "#4e35b1" : "#f4f4f6", color: current === 2 ? "#fff" : "#4e35b1" }} onClick={handeAddtoCart}>ADD TO CART</button>
                </div>
            </div>
        </>
    )
}

export default Salad;
