import React, { useContext, useState } from 'react'
import { userContext } from '../../../context/Usercontext';
import '../../../css/salad.css'

const steps = [
    {
        title: 'First'
    },
    {
        title: 'Second'
    },
    {
        title: 'Last'
    },
];

function Sides({ sides, sidesItem1, setSidesItem1, sidesItem2, setSidesItem2, qty, note, setQty, setNote, handeAddtoCart, setProductDetails, current, setCurrent }) {
    const { selectedItems, setSelectedItems } = useContext(userContext)

    const items = steps.map((item) => ({
        key: item.title,
        title: item.title,
    }));

    const handleItem1 = (item) => {
        if (sidesItem1 === item) {
            setSidesItem1(null);
        } else {
            setSidesItem1(item);
        }

    };



    const handleItem2 = (item) => {
        if (Toast === item) {
            setToast(null);
        } else {
            setToast(item);
        }
    }

    console.log("sidesItem1", sidesItem1);
    console.log("Toast", Toast);
    const next = () => {
        setCurrent(current + 1)
        setSelectedItems([sidesItem1, Toast])
    }

    console.log("selectedItems", selectedItems);
    return (
        <>
            <div className='salad'>
                {/* <Steps current={current} items={items} /> */}
                {current === 0 ?
                    <>
                        <div style={{ display: "flex", justifyContent: "flex-end", margin: "20px 0" }}>
                            <div>
                                {current < steps.length - 1 && (
                                    <button style={{ background: sidesItem1 ? "#4e35b1" : "#f4f4f6", color: sidesItem1 ? "#fff" : "#4e35b1" }} onClick={() => { setCurrent(current + 1) }}>
                                        Next
                                    </button>
                                )}
                            </div>
                        </div>

                        <p className='selectDesc'>Please Select 1 item <span>(Required)</span></p>

                        <div className='item-outer'>
                            {sides.map((item, index) => (
                                <label key={index} className='item'>
                                    <input type="checkbox" onChange={() => handleItem1(item)} checked={sidesItem1 === item} />
                                    <span style={{ marginLeft: "20px" }}>{item.name} [{item.price}]</span>
                                </label>
                            ))}
                        </div>
                    </>
                    : ""}

                {current === 1 ?
                    <>
                        <div style={{ display: "flex", justifyContent: "space-between", margin: "20px 0" }}>
                            <div>
                                <button onClick={() => { setCurrent(current - 1) }} >
                                    Previous
                                </button>

                            </div>
                            {current < steps.length - 1 && (
                                <button disabled={!Toast} style={{ background: Toast ? "#4e35b1" : "#f4f4f6", color: Toast ? "#fff" : "#4e35b1" }} onClick={() => next()}>
                                    Next
                                </button>
                            )}
                        </div>
                        <p className='selectDesc' >Please Select 1 item <span>(Required)</span></p>

                        <div className='item-outer'>
                            {saladToast.map((item, index) => (
                                <label className='item' key={index}>
                                    <input type="checkbox" onChange={() => handleItem2(item)} checked={Toast === item} />
                                    <span style={{ marginLeft: "20px" }}>{item.name}</span>
                                </label>
                            ))}
                        </div>
                    </>
                    : ""}

                {current === 2 ?
                    <>
                        <div style={{ display: "flex", justifyContent: "flex-start", margin: "20px 0" }}>
                            <div>
                                <button onClick={() => { setCurrent(current - 1) }} >
                                    Previous
                                </button>
                            </div>
                        </div>

                        <div className='note-outer'>
                            <span>Notes</span>
                            <textarea className='note' value={note} onChange={(e) => setNote(e.target.value)}></textarea>
                        </div>

                        <div className='qty-inc-dec'>
                            <div onClick={() => { if (qty > 1) { setQty(qty - 1) } }}>-</div>
                            <input type="text" value={qty} readOnly onChange={(e) => setQty(e.target.value)} />
                            <div onClick={() => setQty(qty + 1)}>+</div>
                        </div>
                    </>
                    : ""}

                <div className='model-footer'>
                    <button onClick={() => setProductDetails(false)} className='cancle'>CANCEL</button>

                    <button disabled={current != 2} style={{ background: current === 2 ? "#4e35b1" : "#f4f4f6", color: current === 2 ? "#fff" : "#4e35b1" }} onClick={handeAddtoCart}>ADD TO CART</button>
                </div>
            </div>
        </>
    )
}

export default Sides;
