import React, { useContext, useState } from 'react'
import { Steps } from 'antd';
import { userContext } from '../../../context/Usercontext';
const steps = [
    {
        title: 'First'
    },
    {
        title: 'Second'
    },
    {
        title: 'Last'
    },
];

function Salad({ salad, saladItem, setSaladItem }) {

    const [current, setCurrent] = useState(0);
    const { selectedItems, setSelectedItems } = useContext(userContext)
    const items = steps.map((item) => ({
        key: item.title,
        title: item.title,
    }));


    console.log("selectedItems", selectedItems);
    const handleCheckboxChange = (item) => {
        const isSelected = selectedItems.some(selectedItem => selectedItem.id === item.id);

        if (isSelected) {
            // If selected, remove the item from the array
            const updatedItems = selectedItems.filter(selectedItem => selectedItem.id !== item.id);
            setSelectedItems(updatedItems);
        } else {
            // If not selected, add the item to the array
            setSelectedItems([...selectedItems, item]);
        }

    };

    console.log("saladItem",saladItem);
    const next = () => {
        setCurrent(current + 1)
    }

    return (
        <>
            <div className='salad'>
                <Steps current={current} items={items} />
                <div style={{ display: "flex", justifyContent: "space-between", margin: "20px 0" }}>
                    {current > 0 && (
                        <button onClick={() => { setCurrent(current - 1) }} >
                            Previous
                        </button>
                    )}
                    {current < steps.length - 1 && (
                        <button type="primary" onClick={() => next}>
                            Next
                        </button>
                    )}
                </div>

                <p style={{ textAlign: "center", fontSize: "14px", marginBottom: "10px" }}>Please Select 1 item</p>

                {
                    salad.map((item, index) => (
                        <div key={index} style={{ width: "100%", maxWidth: "40%", margin: "0 auto 5px auto" }}>
                            <label>
                                <input type="checkbox" onChange={() => handleCheckboxChange(item)} checked={saladItem === item} />
                                <span style={{ marginLeft: "20px" }}>{item.name} [{item.price}]</span>
                            </label>
                        </div>
                    ))
                }
            </div>
        </>
    )
}

export default Salad;
