import React, { useContext, useState } from 'react'
import { Steps } from 'antd';
import { userContext } from '../../../context/Usercontext';
const steps = [
    {
        title: 'First'
    },
    {
        title: 'Second'
    },
    {
        title: 'Last'
    },
];

function Salad({ salad, saladItem, setSaladItem }) {

    const [current, setCurrent] = useState(0);
    const { selectedItems, setSelectedItems } = useContext(userContext)
    const items = steps.map((item) => ({
        key: item.title,
        title: item.title,
    }));



    const handleCheckboxChange = (item) => {

        if (saladItem === item) {
            setSaladItem(null);
        } else {
            setSaladItem(item);
        }


        const selectedIndex = saladItem.findIndex(selected => Object.values(selected)[0] === item);
        let updatedsaladItem = [...saladItem];
        if (selectedIndex === -1) {
            updatedsaladItem.push({ [updatedsaladItem.length]: item });
        } else {
            updatedsaladItem.splice(selectedIndex, 1);
        }

        const updatedObjects = updatedSelectedItems.map((obj, index) => {
            const key = Object.keys(obj)[0];
            return { [index]: obj[key] };
        });

        setSelectedItems(updatedObjects);
    };

    return (
        <>
            <div className='salad'>
                <Steps current={current} items={items} />
                <div style={{ display: "flex", justifyContent: "space-between", margin: "20px 0" }}>
                    {current > 0 && (
                        <button onClick={() => { setCurrent(current - 1) }} >
                            Previous
                        </button>
                    )}
                    {current < steps.length - 1 && (
                        <button type="primary" onClick={() => { setCurrent(current + 1) }}>
                            Next
                        </button>
                    )}
                </div>

                <p style={{ textAlign: "center", fontSize: "14px", marginBottom: "10px" }}>Please Select 1 item</p>

                {
                    salad.map((item, index) => (
                        <div key={index} style={{ width: "100%", maxWidth: "40%", margin: "0 auto 5px auto" }}>
                            <label>
                                <input type="checkbox" onChange={() => handleCheckboxChange(item)} checked={selectedItems === item} />
                                <span style={{ marginLeft: "20px" }}>{item.name} [{item.price}]</span>
                            </label>
                        </div>
                    ))
                }
            </div>
        </>
    )
}

export default Salad;
