import React, { useContext, useEffect, useState } from 'react'
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Customize from './Customize';
import { Modal } from 'antd';
import axios from 'axios';
import { userContext } from '../../context/Usercontext';

function ProductDetails({ pdetail, setProductDetails,categoryDescription }) {
    console.log("pdetail", categoryDescription);
    const { billData, setBillData } = useContext(userContext)
    const [customize, setCustomize] = useState(false)
    const [crust, setCrust] = useState([]);
    const [size, setSize] = useState([]);
    const [selectsize, setSelectSize] = useState();
    const [qty, setQty] = useState(1)
    const [selectcrust, setSelectCrust] = useState();
    const [note, setNote] = useState("")
    const [selectedItem, setSelectedItem] = useState([])

    // ADD TO CART 
    const handeAddtoCart = () => {
        const params = {
            size: selectsize,
            crust: selectcrust,
            note: note,
            count: qty,
            customize: selectedItem,
            produtDetail: pdetail,
            productTotal: (selectsize.sizePriceX1 * qty)
        }
        setProductDetails(false)
        setNote("")
        setQty(1)
        setBillData([...billData, params])

    }



    const handleCustomize = () => {
        setCustomize(true)
    }


    // GET SIZE 
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_URL}/api/PizzaSize/ByProductId?productId=${pdetail.productId}`).then((res) => {
            console.log(res);
            setSize(res.data)
        })
    }, [])


    // GET CRUST
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_URL}/api/Modifier/Crusts?sizeId=1&crustId=0&productId=${pdetail.productId}`).then((res) => {
            setCrust(res.data)
        })
    }, [])

    return (
        <>
            <div className='product-details-outer'>
                <h3 className='pname'>{pdetail.productName}</h3>
                <h6 className='ptype'>{pdetail.productDescription}</h6>
                <div className='row'>
                    <div className='col-lg-6'>
                        <Box
                            component="form"
                            sx={{
                                '& .MuiTextField-root': { m: 1, width: '100%' },
                            }}
                            noValidate
                            autoComplete="off"
                        >
                            <TextField
                                id="outlined-select-size"
                                select
                                label="Size"
                                SelectProps={{
                                    native: true,
                                }}
                                helperText="Please select size"
                            >
                                <option value={selectsize} selected disabled>select</option>
                                {size.map((option) => (
                                    <option key={option.sizeName} value={option.sizeName} onClick={() => setSelectSize(option)}>
                                        {option.sizeName} [{option.sizePriceX1}]
                                    </option>
                                ))}
                            </TextField>
                        </Box>
                    </div>
                    <div className='col-lg-6'>
                        <Box
                            component="form"
                            sx={{
                                '& .MuiTextField-root': { m: 1, width: '100%' },
                            }}
                            noValidate
                            autoComplete="off"
                        >
                            <TextField
                                id="outlined-select-crust"
                                select
                                label="Crust"
                                onChange={(e) => setSelectCrust(e.target.value)}
                                SelectProps={{
                                    native: true,
                                }}
                                helperText="Please select crust"
                            >
                                <option value={selectsize} selected disabled>select</option>
                                {crust.map((option) => (
                                    <option key={option.modifierName} value={option.modifierName}>
                                        {option.modifierName}
                                    </option>
                                ))}
                            </TextField>
                        </Box>
                    </div>
                </div>
                <div className='note-outer'>
                    <span>Notes</span>
                    <textarea className='note' onChange={(e) => setNote(e.target.value)}></textarea>
                </div>
                <div className='qty-inc-dec'>
                    <div onClick={() => { if (qty > 1) { setQty(qty - 1) } }}>-</div>
                    <input type="text" value={qty} readOnly onChange={(e) => setQty(e.target.value)} />
                    <div onClick={() => setQty(qty + 1)}>+</div>
                </div>
                <div className='model-footer'>
                    <button className='cancle'>CANCLE</button>
                    <div onClick={handleCustomize}>CUSTOMIZE</div>
                    <button onClick={handeAddtoCart}>ADD TO CART</button>
                </div>
            </div>

            <Modal open={customize} onCancel={() => setCustomize(false)} width={500}>
                <Customize {...{ pdetail, selectsize, selectedItem, setSelectedItem, setCustomize }} />
            </Modal>
        </>
    )
}

export default ProductDetails;
