import React, { useContext, useState } from 'react'
import { Steps } from 'antd';
import { userContext } from '../../../context/Usercontext';
const steps = [
    {
        title: 'First'
    },
    {
        title: 'Second'
    },
    {
        title: 'Last'
    },
];

function Salad({ salad }) {

    const [current, setCurrent] = useState(0);
    const { selectedItems, setSelectedItems } = useContext(userContext)
    const [selectedItem, setSelectedItem] = useState(null);
    const items = steps.map((item) => ({
        key: item.title,
        title: item.title,
    }));



    const handleCheckboxChange = (item) => {

        if (selectedItem === item) {
            setSelectedItem(null);
        } else {
            setSelectedItem(item);
        }


        const selectedIndex = selectedItems.findIndex(selected => Object.values(selected)[0] === item);
        let updatedSelectedItems = [...selectedItems];
        if (selectedIndex === -1) {
            updatedSelectedItems.push({ [updatedSelectedItems.length]: item });
        } else {
            updatedSelectedItems.splice(selectedIndex, 1);
        }

        const updatedObjects = updatedSelectedItems.map((obj, index) => {
            const key = Object.keys(obj)[0];
            return { [index]: obj[key] };
        });

        setSelectedItems(updatedObjects);
    };

    return (
        <>
            <div className='salad'>
                <Steps current={current} items={items} />
                <div style={{ display: "flex", justifyContent: "space-between", margin: "20px 0" }}>
                    {current > 0 && (
                        <button onClick={() => { setCurrent(current - 1) }} >
                            Previous
                        </button>
                    )}
                    {current < steps.length - 1 && (
                        <button type="primary" onClick={() => { setCurrent(current + 1) }}>
                            Next
                        </button>
                    )}
                </div>

                <p style={{ textAlign: "center", fontSize: "14px", marginBottom: "10px" }}>Please Select 1 item</p>

                {
                    salad.map((item, index) => (
                        <div>
                            <label className="checkbox-inner">
                                <input type="checkbox" className="form-checkbox" onChange={() => handleCheckboxChange(item)} checked={selectedItem === item} />
                                <span className="item">{item.name}</span>
                            </label>
                        </div>
                    ))
                }
            </div>
        </>
    )
}

export default Salad;
