import React, { useContext, useEffect, useState } from 'react'
import '../css/sidebar.css'
import { Link } from 'react-router-dom';

import { userContext } from '../context/Usercontext';

function Sidebar() {
  const [selectedItem, setSelectedItem] = useState(null);
  const { data, fatchData } = useContext(userContext);

  useEffect(() => {
    fatchData();
  }, [])

  return (
    <>
      <div className='sidebar-outer'>
        <ul className='catagorys'>
          {
            data.map(({ categoryDescription, id }, index) => (
              <li className='link'>
                <Link to={"/" + categoryDescription + "/" + id} className={selectedItem != index ? 'catagory-name selected' : 'catagory-name'} >{categoryDescription}</Link>
              </li>
            ))
          }
        </ul>
      </div>
    </>
  )
}

export default Sidebar;
