import React, { useContext, useEffect, useState } from 'react'
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Customize from './Customize';
import { Modal } from 'antd';
import axios from 'axios';
import Pizza from './PIZZA/Pizza'
import { userContext } from '../../context/Usercontext';
import Desserts from './DESSERTS/Desserts';
import Salad from './Salad/Salad';

function ProductDetails({ pdetail, setProductDetails, categoryDescription }) {
    console.log("categoryDescription :", categoryDescription);
    console.log("pdetail :", pdetail);
    const { billData, setBillData, selectedItem, setSelectedItem,selectedItems } = useContext(userContext)
    const [customize, setCustomize] = useState(false)
    const [crust, setCrust] = useState([]);
    const [size, setSize] = useState([]);
    const [selectsize, setSelectSize] = useState();
    const [selectcrust, setSelectCrust] = useState();
    const [saladItem,setSaladItem] = useState
    const [qty, setQty] = useState(1)
    const [note, setNote] = useState("")
    const [salad, setSalad] = useState([])

    // ADD TO CART 
    const handeAddtoCart = () => {
        const params = {
            // size: selectsize,
            // crust: selectcrust,
            note: note,
            qty: qty,
            selectedItem: selectedItems,
            produtDetail: pdetail,
            productTotal: (categoryDescription === "DESSERTS") ? (pdetail.price * qty) : ''
        }
        setProductDetails(false)
        setNote("")
        setQty(1)
        setBillData([...billData, params])

    }



    const handleCustomize = () => {
        setCustomize(true)
    }


    // GET SIZE 
    var getSize = () => {
        axios.get(`${process.env.REACT_APP_URL}/api/PizzaSize/ByProductId?productId=${pdetail.productId}`).then((res) => {
            console.log(res);
            setSize(res.data)
        })
            .catch((err) => {
                console.log(err);
            })
    }


    // SALADS 
    const salads = () => {
        axios.get(`${process.env.REACT_APP_URL}/api/Variation/ByProductId?productId=${pdetail.productId}&parentId=0`).then((res) => {
            console.log("res", res);
            setSalad(res.data);
        })
    }


    // 
    useEffect(() => {
        if (categoryDescription === "PIZZA") {
            getSize();
        }
        else if (categoryDescription === "SALADS") {
            salads();
        }
    }, [])


    // GET CRUST
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_URL}/api/Modifier/Crusts?sizeId=1&crustId=0&productId=${pdetail.productId}`).then((res) => {
            setCrust(res.data)
        })
            .catch((err) => {
                console.log(err);
            })
    }, [])

    return (
        <>
            <div className='product-details-outer'>
                <h3 className='pname'>{pdetail.productName}</h3>
                <h6 className='ptype'>{pdetail.productDescription}</h6>

                {/* PIZZA */}
                {categoryDescription === "PIZZA" ? <Pizza {...{ size, crust, selectsize, setSelectSize, setSelectCrust, note, setNote, qty, setQty }} /> : ""}

                {/* SALADS */}
                {categoryDescription === "SALADS" ? <Salad {...{ salad }} /> : ""}


                {/* DESSERTS */}
                {categoryDescription === "DESSERTS" ? <Desserts {...{ note, setNote, qty, setQty }} /> : ""}


                <div className='model-footer'>
                    <button onClick={() => setProductDetails(false)} className='cancle'>CANCLE</button>

                    {/* PIZZA */}
                    {categoryDescription === "PIZZA" ?
                        <div onClick={handleCustomize}>CUSTOMIZE</div>
                        : ""}
                    <button onClick={handeAddtoCart}>ADD TO CART</button>
                </div>
            </div>



            {/* PIZZA */}
            {categoryDescription === "PIZZA" ?
                <Modal open={customize} onCancel={() => setCustomize(false)} width={500}>
                    <Customize {...{ pdetail, selectsize, selectedItem, setSelectedItem, setCustomize }} />
                </Modal>
                : ""}

        </>
    )
}

export default ProductDetails;
